import groovy.json.JsonSlurper
// get a reference to the library TestSuite library = testRunner.testCase.testSuite.project.testSuites["Library"]
// find the module within the library module = library.testCases["Booking"].testSteps["PreConfirm"]
// initialise the library; which places an instance of Example in the context module.run(testRunner, context)
// get the instance of example from the context. def confirmBooking = context.booking
log.info confirmBooking
responseContent = testRunner.testCase.getTestStepByName("102 - Prebooking (2PAX)").getPropertyValue("response") slurperresponse = new JsonSlurper().parseText(responseContent)
confirmBooking.TransferBooking(slurperresponse,responseContent) confirmBooking.TransferPagos(slurperresponse,responseContent)

import groovy.json.JsonSlurper
responseContent = testRunner.testCase.getTestStepByName("102 - Prebooking (2PAX)").getPropertyValue("response") slurperresponse = new JsonSlurper().parseText(responseContent)
//Info booking def desbookingCode = slurperresponse.bookingCode def destotalPrice = slurperresponse.totalPrice def desdescription = slurperresponse.description
log.info destotalPrice + " "+ desbookingCode + " "+ desdescription
//Info Payments def desctotalPriceWithFee = slurperresponse.totalPricesWithFees[0].totalPriceWithFee def desccardFee = slurperresponse.totalPricesWithFees[0].cardFee def descdeposit = slurperresponse.totalPricesWithFees[0].deposit def descdepositCardFee = slurperresponse.totalPricesWithFees[0].depositCardFee def desccodPaymentMethod = slurperresponse.totalPricesWithFees[0].codPaymentMethod def descstrDescription = slurperresponse.totalPricesWithFees[0].strDescription def desccodPaymentType = slurperresponse.totalPricesWithFees[0].codPaymentType def descpctPayment = slurperresponse.totalPricesWithFees[0].pctPayment
def Confirm = testRunner.testCase.getTestStepByName("CONFIRM")
Confirm.setPropertyValue('preBookingId',desbookingCode.toString()) Confirm.setPropertyValue('price',destotalPrice.toString()) Confirm.setPropertyValue('description',desdescription.toString())
Confirm.setPropertyValue('pctotalPriceWithFee',desctotalPriceWithFee.toString()) Confirm.setPropertyValue('pcardFee',desccardFee.toString()) Confirm.setPropertyValue('deposit',descdeposit.toString()) Confirm.setPropertyValue('pdepositCardFee',descdepositCardFee.toString()) Confirm.setPropertyValue('pcodPaymentMethod',desccodPaymentMethod.toString()) Confirm.setPropertyValue('pstrDescription',descstrDescription.toString()) Confirm.setPropertyValue('pcodPaymentType',desccodPaymentType.toString()) Confirm.setPropertyValue('ppctPayment',descpctPayment.toString())

