con:config.script:
// get a reference to the library TestSuite
library = testRunner.testCase.testSuite.project.testSuites["Library"]

// find the module within the library 
module = library.testCases["Ancillaries"].testSteps["Ancillaries"]

// initialise the library; which places an instance of Example in the context
module.run(testRunner, context)

// get the instance of example from the context.
def ancillary = context.ancillary

// run the method, with parameter
//IDA
def flight_ida = context.expand('${#Project#flightNumber_ida}')
ancillary.delete(flight_ida, "LUG", 1)
ancillary.delete(flight_ida, "SEO", 1)

//VUELTA
def flight_vuelta = context.expand('${#Project#flightNumber_vuelta}')
ancillary.delete(flight_vuelta, "LUG", 1)
ancillary.delete(flight_vuelta, "SEO", 1)

